import React from "react";

function Message() {
    const messages = [
        { username: "Asepkuda", handle: "@asepkudabu", message: "ada barang barunya" },
        { username: "Rotibakar", handle: "@gagakoseq", message: "kak keranjang ada" },
        { username: "Lengkuas", handle: "@lengkuas_uhh", message: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua." },
        { username: "nahhh", handle: "@huhu", message: "ke kanan" },
    ];

    return (
        <div className="bg-black p-6 rounded-md shadow-md w-full max-w-lg mx-auto">
            <h1 className="text-xl font-bold text-white mb-4">Direct Message</h1>
            <ul className="list-none">
                {messages.map((msg, index) => (
                    <li key={index} className="border-b border-gray-700 py-4 flex ">
                        <div className="w-12 h-12 bg-gray-700 rounded-full flex-shrink-0"></div>
                        <div className="ml-4 flex-1">
                            <p className="text-sm font-bold text-white">
                                {msg.username} <span className="text-gray-400 text-xs">{msg.handle}</span>
                            </p>
                            <p className="text-gray-300 text-sm mt-1">{msg.message}</p>
                        </div>
                        {/* <div className="w-3 h-3 bg-red-600 rounded-full flex-shrink-0"></div> */}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Message;
