import React from "react";

const AuthLayout = ({ children, type }) => {
  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="w-full max-w-md bg-white shadow-md rounded-lg p-8">
        <h1 className="text-2xl font-bold text-center mb-4">
          {type === "signin" ? "Log In to Your Account" : "Create Your Account"}
        </h1>

        {/* Render child form */}
        {children}

        <div className="flex items-center justify-between my-4">
          <hr className="w-1/3 border-gray-300" />
          <span className="text-sm text-gray-500">OR</span>
          <hr className="w-1/3 border-gray-300" />
        </div>

        {/* Social Media Buttons */}
        {type === "si"}
        <div className="flex justify-center gap-4">
          <button className="flex items-center justify-center w-10 h-10 border rounded-full">
            <img src="/google-icon.png" alt="Google" className="w-6 h-6" />
          </button>
          <button className="flex items-center justify-center w-10 h-10 border rounded-full">
            <img src="/facebook-icon.png" alt="Facebook" className="w-6 h-6" />
          </button>
        </div>

        {/* Footer Links */}
        <p className="text-sm text-center text-gray-500 mt-4">
          {type === "signin" ? (
            <>
              New to For U?{" "}
              <a href="/signup" className="text-blue-600 font-medium hover:underline">
                Join Now
              </a>
            </>
          ) : (
            <>
              Already have an account?{" "}
              <a href="/signin" className="text-blue-600 font-medium hover:underline">
                Sign In
              </a>
            </>
          )}
        </p>
      </div>
    </div>
  );
};

export default AuthLayout;
